import React, { useState, useEffect } from 'react';
import Flashcard from './component/Flashcard';
import Navigation from './component/Navigation';
import axios from 'axios';

const App = () => {
  const [flashcards, setFlashcards] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    axios.get('/api/flashcards').then(response => {
      setFlashcards(response.data);
    });
  }, []);

  const prevFlashcard = () => {
    setCurrentIndex(currentIndex > 0 ? currentIndex - 1 : flashcards.length - 1);
  };

  const nextFlashcard = () => {
    setCurrentIndex((currentIndex + 1) % flashcards.length);
  };

  return (
    <div className="app">
      {flashcards.length > 0 && <Flashcard flashcard={flashcards[currentIndex]} />}
      <Navigation prevFlashcard={prevFlashcard} nextFlashcard={nextFlashcard} />
    </div>
  );
};

export default App;
