import React, { useState, useEffect } from 'react';
import Flashcard from './component/Flashcard';
import FlashcardNavigation from './component/FlashcardNavigation';
import AdminDashboard from './component/AdminDashboard';

function App() {
  const [flashcards, setFlashcards] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // Fetch flashcards from the backend when the component mounts
    fetchFlashcards();
  }, []);

  const fetchFlashcards = async () => {
    const response = await fetch('/api/flashcards');
    const data = await response.json();
    setFlashcards(data);
  };

  const nextCard = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % flashcards.length);
  };

  const prevCard = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + flashcards.length) % flashcards.length);
  };

  return (
    <div className="App">
      <h1>Flashcard Learning Tool</h1>
      {flashcards.length > 0 && (
        <>
          <Flashcard flashcard={flashcards[currentIndex]} />
          <FlashcardNavigation onNext={nextCard} onPrev={prevCard} />
        </>
      )}
      <AdminDashboard onUpdate={fetchFlashcards} />
    </div>
  );
}

export default App;
