import React, { useState, useEffect, useRef } from 'react';
import './Flashcard.css'; // Import CSS for styling

export default function Flashcard({ flashcard }) {
  const [flip, setFlip] = useState(false);
  const [height, setHeight] = useState('initial');

  const frontEl = useRef();
  const backEl = useRef();

  // Function to set the maximum height of the card
  function setMaxHeight() {
    const frontHeight = frontEl.current.getBoundingClientRect().height;
    const backHeight = backEl.current.getBoundingClientRect().height;
    setHeight(Math.max(frontHeight, backHeight, 100)); // Default height 100px if both are 0
  }

  useEffect(() => {
    // Set height when the flashcard content changes
    setMaxHeight();
  }, [flashcard.question, flashcard.answer, flashcard.options]);

  useEffect(() => {
    // Add resize event listener
    window.addEventListener('resize', setMaxHeight);
    return () => {
      // Clean up event listener
      window.removeEventListener('resize', setMaxHeight);
    };
  }, []);

  return (
    <div
      className={`card ${flip ? 'flip' : ''}`}
      style={{ height: height }}
      onClick={() => setFlip(!flip)}
    >
      <div className="front" ref={frontEl}>
        <p>{flashcard.question}</p>
        <div className="flashcard-options">
          {flashcard.options.map(option => (
            <div className="flashcard-option" key={option}>{option}</div>
          ))}
        </div>
      </div>
      <div className="back" ref={backEl}>
        <p>{flashcard.answer}</p>
      </div>
    </div>
  );
}
