import React, { useState, useEffect, useRef } from 'react';
import FlashcardList from './component/FlashcardList';
import './App.css'
import axios from 'axios'

function App() {
  const [flashcards, setFlashcards] = useState([]);
  const [categories, setCategories] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0); // State for current flashcard index

  const categoryEl = useRef();
  const amountEl = useRef();

  useEffect(() => {
    axios
      .get('https://opentdb.com/api_category.php')
      .then(res => {
        setCategories(res.data.trivia_categories);
      });
  }, []);

  function decodeString(str) {
    const textArea = document.createElement('textarea');
    textArea.innerHTML = str;
    return textArea.value;
  }

  function handleSubmit(e) {
    e.preventDefault();
    axios
      .get('https://opentdb.com/api.php', {
        params: {
          amount: amountEl.current.value,
          category: categoryEl.current.value,
        },
      })
      .then(res => {
        setFlashcards(res.data.results.map((questionItem, index) => {
          const answer = decodeString(questionItem.correct_answer);
          const options = [
            ...questionItem.incorrect_answers.map(a => decodeString(a)),
            answer,
          ];
          return {
            id: `${index}-${Date.now()}`,
            question: decodeString(questionItem.question),
            answer: answer,
            options: options.sort(() => Math.random() - 0.5),
          };
        }));
        setCurrentIndex(0); // Reset index when new flashcards are loaded
      });
  }

  function handleNext() {
    setCurrentIndex(prevIndex => (prevIndex + 1) % flashcards.length);
  }

  function handlePrevious() {
    setCurrentIndex(prevIndex => (prevIndex - 1 + flashcards.length) % flashcards.length);
  }

  return (
    <>
      <form className="header" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="category">Category</label>
          <select id="category" ref={categoryEl}>
            {categories.map(category => (
              <option value={category.id} key={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="amount">Number of Questions</label>
          <input
            type="number"
            id="amount"
            min="1"
            step="1"
            defaultValue={10}
            ref={amountEl}
          />
        </div>
        <div className="form-group">
          <button className="btn">Generate</button>
        </div>
      </form>
      <div className="container">
        {flashcards.length > 0 && (
          <>
            <div className="flashcard">
              <div className="question">
                {flashcards[currentIndex].question}
              </div>
              <div className="options">
                {flashcards[currentIndex].options.map((option, index) => (
                  <div key={index} className="option">
                    {option}
                  </div>
                ))}
              </div>
            </div>
            <div className="navigation">
              <button onClick={handlePrevious} disabled={flashcards.length <= 1}>
                Back
              </button>
              <button onClick={handleNext} disabled={flashcards.length <= 1}>
                Next
              </button>
            </div>
          </>
        )}
      </div>
    </>
  );
}

export default App;
